dunes-script-module.script:
//*****Fetching RefreshToken using VRA credentials*****

//Endpoint url
var url = "/csp/gateway/am/api/login?access_token";

//Body
var requestBody = '{ "username":"' + username + '", "password":"' + password + '" , "domain":"coke.sqa-horizon.local"}';

//Request
var request = restHost.createRequest(method, url, requestBody);

//Headers
request.setHeader("Accept", "application/json");
request.setHeader("Content-Type","application/json");
request.setHeader("Cache-Control","no-cache");

//Response
var response = request.execute();

//Formatting response
if(requestResponse.statusCode == 200) {
    refreshToken = JSON.parse(requestResponse.contentAsString).refresh_token;
    System.log("refresh_token : " + refreshToken);    
}else{
    System.error("Bearer Token request has returned an error status code : " + requestResponse.statusCode);
    throw "Error : " + requestResponse.statusCode;
}


//*****Fetching BearerToken using RefreshToken*****

//Endpoint url
url = "/iaas/api/login";

//Body
requestBody = '{ "refreshToken":"' + refreshToken + '"}';

//Request
request = restHost.createRequest(method, url, requestBody);

//Headers
request.setHeader("Accept", "application/json");
request.setHeader("Content-Type", "application/json");
request.setHeader("Cache-Control", "no-cache");

//Response
var response = request.execute();

//Formatting response
if(requestResponse.statusCode == 200) {
    bearerToken = JSON.parse(requestResponse.contentAsString).token;
    System.log("bearer_token : "+bearerToken);   
    return bearerToken;
}else{
    System.error("Bearer Token request has returned an error status code : " + requestResponse.statusCode);
    throw "Error : " + requestResponse.statusCode;
}
