dunes-script-module.script:
if( region.toUpperCase() == "UK" ){
	return ("UK")
}else if(region.toUpperCase() == "CANADA"){
	return ("CA")
}else if(region.toUpperCase() == "LAB"){
	return ("LAB")
}else if(region.toUpperCase() == "CHINA"){
	return ("CN")
}else if(region.toUpperCase() == "HONG KONG"){
	return ("HK")
}else if(region.toUpperCase() == "INDIA"){
	return ("IN")
}else if(region.toUpperCase() == "JAPAN"){
	return ("JP")
}else if(region.toUpperCase() == "SINGAPORE"){
	return ("SG")
}else {
	throw("Could not convert to short region code. Unknown region: " + region + ".")
}
