workflow-item.script:
//var url="/approval/api/approvals";
//var url = "/iaas/api/cloud-accounts";

//Body
//var requestBody = '';

//Request
var request = restHost.createRequest(method, url, requestBody);

//Header
request.setHeader("Authorization", "Bearer "+bearerToken);
request.setHeader("Accept", "application/json");
request.setHeader("Content-Type", "application/json");
request.setHeader("Cache-Control", "no-cache");

//Response
var response = request.execute();
if(response.statusCode == 200) {    
    System.log("Response Code: "+response.statusCode) ;
    System.log("Response Body: " + response.contentAsString);
    result = new Properties();
    result.put("statusCode", response.statusCode);
    result.put("contentLength", response.contentLength);
    result.put("headers", response.getAllHeaders());
    result.put("contentAsString", response.contentAsString);
}else{
    System.error("Request has returned an error status code : " + response.statusCode);
    throw "Error : " + response.statusCode;
}
